import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertProjectSchema, insertAssetSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      // For demo purposes, return a test user
      const user = await storage.getUser('test-user-123');
      if (user) {
        res.json(user);
      } else {
        res.status(401).json({ message: "Unauthorized" });
      }
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Update user preferences
  app.patch('/api/auth/user/preferences', async (req: any, res) => {
    try {
      const userId = 'test-user-123'; // Demo user
      const { theme, language } = req.body;

      const user = await storage.updateUserPreferences(userId, { theme, language });
      res.json(user);
    } catch (error) {
      console.error("Error updating user preferences:", error);
      res.status(500).json({ message: "Failed to update preferences" });
    }
  });

  // Project routes
  app.get('/api/projects', async (req: any, res) => {
    try {
      const projects = await storage.getProjects('test-user-123');
      res.json(projects);
    } catch (error) {
      console.error("Error fetching projects:", error);
      res.status(500).json({ message: "Failed to fetch projects" });
    }
  });

  app.get('/api/projects/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const projectId = parseInt(req.params.id);
      const project = await storage.getProject(projectId, userId);

      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      res.json(project);
    } catch (error) {
      console.error("Error fetching project:", error);
      res.status(500).json({ message: "Failed to fetch project" });
    }
  });

  app.post('/api/projects', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const projectData = insertProjectSchema.parse({
        ...req.body,
        ownerId: userId,
      });

      const project = await storage.createProject(projectData);
      res.status(201).json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid project data", errors: error.errors });
      }
      console.error("Error creating project:", error);
      res.status(500).json({ message: "Failed to create project" });
    }
  });

  app.put('/api/projects/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const projectId = parseInt(req.params.id);
      const projectData = insertProjectSchema.partial().parse(req.body);

      const project = await storage.updateProject(projectId, projectData, userId);

      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      res.json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid project data", errors: error.errors });
      }
      console.error("Error updating project:", error);
      res.status(500).json({ message: "Failed to update project" });
    }
  });

  app.delete('/api/projects/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const projectId = parseInt(req.params.id);
      const deleted = await storage.deleteProject(projectId, userId);

      if (!deleted) {
        return res.status(404).json({ message: "Project not found" });
      }

      res.status(204).send();
    } catch (error) {
      console.error("Error deleting project:", error);
      res.status(500).json({ message: "Failed to delete project" });
    }
  });

  // Asset routes
  app.get('/api/projects/:projectId/assets', async (req: any, res) => {
    try {
      const projectId = parseInt(req.params.projectId);
      const assets = await storage.getAssets(projectId, 'test-user-123');
      res.json(assets);
    } catch (error) {
      console.error("Error fetching assets:", error);
      res.status(500).json({ message: "Failed to fetch assets" });
    }
  });

  app.get('/api/assets/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const assetId = parseInt(req.params.id);
      const asset = await storage.getAsset(assetId, userId);

      if (!asset) {
        return res.status(404).json({ message: "Asset not found" });
      }

      res.json(asset);
    } catch (error) {
      console.error("Error fetching asset:", error);
      res.status(500).json({ message: "Failed to fetch asset" });
    }
  });

  app.post('/api/projects/:projectId/assets', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const projectId = parseInt(req.params.projectId);

      const assetData = insertAssetSchema.parse({
        ...req.body,
        projectId,
        createdBy: userId,
      });

      const asset = await storage.createAsset(assetData);
      res.status(201).json(asset);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid asset data", errors: error.errors });
      }
      console.error("Error creating asset:", error);
      res.status(500).json({ message: "Failed to create asset" });
    }
  });

  app.put('/api/assets/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const assetId = parseInt(req.params.id);
      const assetData = insertAssetSchema.partial().parse(req.body);

      const asset = await storage.updateAsset(assetId, assetData, userId);

      if (!asset) {
        return res.status(404).json({ message: "Asset not found" });
      }

      res.json(asset);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid asset data", errors: error.errors });
      }
      console.error("Error updating asset:", error);
      res.status(500).json({ message: "Failed to update asset" });
    }
  });

  app.delete('/api/assets/:id', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const assetId = parseInt(req.params.id);
      const deleted = await storage.deleteAsset(assetId, userId);

      if (!deleted) {
        return res.status(404).json({ message: "Asset not found" });
      }

      res.status(204).send();
    } catch (error) {
      console.error("Error deleting asset:", error);
      res.status(500).json({ message: "Failed to delete asset" });
    }
  });

  // Collaboration routes
  app.get('/api/assets/:id/collaborators', async (req: any, res) => {
    try {
      const assetId = parseInt(req.params.id);
      const collaborators = await storage.getActiveCollaborators(assetId);
      res.json(collaborators);
    } catch (error) {
      console.error("Error fetching collaborators:", error);
      res.status(500).json({ message: "Failed to fetch collaborators" });
    }
  });

  app.post('/api/assets/:id/collaborate', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const assetId = parseInt(req.params.id);
      const { cursor } = req.body;

      const session = await storage.createOrUpdateCollaborationSession({
        assetId,
        userId,
        isActive: true,
        cursor,
      });

      res.json(session);
    } catch (error) {
      console.error("Error updating collaboration session:", error);
      res.status(500).json({ message: "Failed to update collaboration session" });
    }
  });

  app.delete('/api/assets/:id/collaborate', async (req: any, res) => {
    try {
      const userId = 'test-user-123';
      const assetId = parseInt(req.params.id);

      await storage.endCollaborationSession(assetId, userId);
      res.status(204).send();
    } catch (error) {
      console.error("Error ending collaboration session:", error);
      res.status(500).json({ message: "Failed to end collaboration session" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  projects,
  assets,
  collaborationSessions,
  type User,
  type UpsertUser,
  type Project,
  type InsertProject,
  type Asset,
  type InsertAsset,
  type CollaborationSession,
  type InsertCollaborationSession,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserPreferences(id: string, preferences: { theme?: string; language?: string }): Promise<User>;

  // Project operations
  getProjects(userId: string): Promise<Project[]>;
  getProject(id: number, userId: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: number, project: Partial<InsertProject>, userId: string): Promise<Project | undefined>;
  deleteProject(id: number, userId: string): Promise<boolean>;

  // Asset operations
  getAssets(projectId: number, userId: string): Promise<Asset[]>;
  getAsset(id: number, userId: string): Promise<Asset | undefined>;
  createAsset(asset: InsertAsset): Promise<Asset>;
  updateAsset(id: number, asset: Partial<InsertAsset>, userId: string): Promise<Asset | undefined>;
  deleteAsset(id: number, userId: string): Promise<boolean>;

  // Collaboration operations
  getActiveCollaborators(assetId: number): Promise<(CollaborationSession & { user: User })[]>;
  createOrUpdateCollaborationSession(session: InsertCollaborationSession): Promise<CollaborationSession>;
  endCollaborationSession(assetId: number, userId: string): Promise<boolean>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserPreferences(id: string, preferences: { theme?: string; language?: string }): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        ...preferences,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Project operations
  async getProjects(userId: string): Promise<Project[]> {
    return await db
      .select()
      .from(projects)
      .where(eq(projects.ownerId, userId))
      .orderBy(desc(projects.updatedAt));
  }

  async getProject(id: number, userId: string): Promise<Project | undefined> {
    const [project] = await db
      .select()
      .from(projects)
      .where(and(eq(projects.id, id), eq(projects.ownerId, userId)));
    return project;
  }

  async createProject(project: InsertProject): Promise<Project> {
    const [newProject] = await db
      .insert(projects)
      .values(project)
      .returning();
    return newProject;
  }

  async updateProject(id: number, project: Partial<InsertProject>, userId: string): Promise<Project | undefined> {
    const [updatedProject] = await db
      .update(projects)
      .set({ ...project, updatedAt: new Date() })
      .where(and(eq(projects.id, id), eq(projects.ownerId, userId)))
      .returning();
    return updatedProject;
  }

  async deleteProject(id: number, userId: string): Promise<boolean> {
    const result = await db
      .delete(projects)
      .where(and(eq(projects.id, id), eq(projects.ownerId, userId)));
    return (result.rowCount ?? 0) > 0;
  }

  // Asset operations
  async getAssets(projectId: number, userId: string): Promise<Asset[]> {
    return await db
      .select()
      .from(assets)
      .innerJoin(projects, eq(assets.projectId, projects.id))
      .where(and(
        eq(assets.projectId, projectId),
        eq(projects.ownerId, userId),
        eq(assets.isDeleted, false)
      ))
      .orderBy(asc(assets.type), asc(assets.name))
      .then(rows => rows.map(row => row.assets));
  }

  async getAsset(id: number, userId: string): Promise<Asset | undefined> {
    const [result] = await db
      .select()
      .from(assets)
      .innerJoin(projects, eq(assets.projectId, projects.id))
      .where(and(
        eq(assets.id, id),
        eq(projects.ownerId, userId),
        eq(assets.isDeleted, false)
      ));
    return result?.assets;
  }

  async createAsset(asset: InsertAsset): Promise<Asset> {
    const [newAsset] = await db
      .insert(assets)
      .values(asset)
      .returning();
    return newAsset;
  }

  async updateAsset(id: number, asset: Partial<InsertAsset>, userId: string): Promise<Asset | undefined> {
    const [updatedAsset] = await db
      .update(assets)
      .set({ ...asset, updatedAt: new Date() })
      .where(and(
        eq(assets.id, id),
        eq(assets.createdBy, userId),
        eq(assets.isDeleted, false)
      ))
      .returning();
    return updatedAsset;
  }

  async deleteAsset(id: number, userId: string): Promise<boolean> {
    const [updatedAsset] = await db
      .update(assets)
      .set({ isDeleted: true, updatedAt: new Date() })
      .where(and(
        eq(assets.id, id),
        eq(assets.createdBy, userId),
        eq(assets.isDeleted, false)
      ))
      .returning();
    return !!updatedAsset;
  }

  // Collaboration operations
  async getActiveCollaborators(assetId: number): Promise<(CollaborationSession & { user: User })[]> {
    const results = await db
      .select()
      .from(collaborationSessions)
      .innerJoin(users, eq(collaborationSessions.userId, users.id))
      .where(and(
        eq(collaborationSessions.assetId, assetId),
        eq(collaborationSessions.isActive, true)
      ))
      .orderBy(desc(collaborationSessions.lastActivity));

    return results.map(result => ({
      ...result.collaboration_sessions,
      user: result.users,
    }));
  }

  async createOrUpdateCollaborationSession(session: InsertCollaborationSession): Promise<CollaborationSession> {
    const [existingSession] = await db
      .select()
      .from(collaborationSessions)
      .where(and(
        eq(collaborationSessions.assetId, session.assetId),
        eq(collaborationSessions.userId, session.userId)
      ));

    if (existingSession) {
      const [updatedSession] = await db
        .update(collaborationSessions)
        .set({
          isActive: session.isActive,
          cursor: session.cursor,
          lastActivity: new Date(),
        })
        .where(eq(collaborationSessions.id, existingSession.id))
        .returning();
      return updatedSession;
    } else {
      const [newSession] = await db
        .insert(collaborationSessions)
        .values(session)
        .returning();
      return newSession;
    }
  }

  async endCollaborationSession(assetId: number, userId: string): Promise<boolean> {
    const [updatedSession] = await db
      .update(collaborationSessions)
      .set({ isActive: false, lastActivity: new Date() })
      .where(and(
        eq(collaborationSessions.assetId, assetId),
        eq(collaborationSessions.userId, userId)
      ))
      .returning();
    return !!updatedSession;
  }
}

export const storage = new DatabaseStorage();

