  onProjectSelect: (project: Project) => void;
  onAssetOpen: (asset: Asset) => void;
  onCreateAsset: (name: string, type: string) => void;
  onDeleteAsset?: (asset: Asset) => void;
  onCreateProject?: (name: string, description?: string) => void;
  onDeleteProject?: (project: Project) => void;
  isLoading: boolean;
}

export interface ProjectExplorerRef {
  openCreateDialog: () => void;
}

const ProjectExplorer = forwardRef<ProjectExplorerRef, ProjectExplorerProps>(({ 
  projects, 
  selectedProject, 
  assets, 
  onProjectSelect, 
  onAssetOpen,
  onCreateAsset,
  onDeleteAsset,
  onCreateProject,
  onDeleteProject,
  isLoading 
}, ref) => {
  const { t } = useLanguage();
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(
    new Set(['vocabulary', 'rulesheets', 'ruleflow', 'rulestest'])
  );
  const [newAssetName, setNewAssetName] = useState("");
  const [newAssetType, setNewAssetType] = useState("");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);

  // Project management state
  const [isCreateProjectDialogOpen, setIsCreateProjectDialogOpen] = useState(false);
  const [newProjectName, setNewProjectName] = useState("");
  const [newProjectDescription, setNewProjectDescription] = useState("");

  useImperativeHandle(ref, () => ({
    openCreateDialog: () => setIsCreateDialogOpen(true)
  }));

  const toggleFolder = (folder: string) => {
    const newExpanded = new Set(expandedFolders);
    if (newExpanded.has(folder)) {
      newExpanded.delete(folder);
    } else {
      newExpanded.add(folder);
    }
    setExpandedFolders(newExpanded);
  };

  const getAssetsByType = (type: string) => {
    return assets.filter(asset => asset.type === type);
  };

  const getAssetIcon = (type: string) => {
    switch (type) {
      case 'vocabulary':
        return <Code className="h-4 w-4 text-github-blue" />;
      case 'rulesheets':
        return <FileText className="h-4 w-4 text-success-green" />;
      case 'ruleflow':
        return <FileText className="h-4 w-4 text-warning-amber" />;
      case 'rulestest':
        return <FileText className="h-4 w-4 text-purple-400" />;
      default:
        return <FileText className="h-4 w-4 text-gray-400" />;
    }
  };

  const handleCreateAsset = () => {
    if (newAssetName && newAssetType) {
      onCreateAsset(newAssetName, newAssetType);
      setNewAssetName("");
      setNewAssetType("");
      setIsCreateDialogOpen(false);
    }
  };

  const handleCreateProject = () => {
    if (newProjectName && onCreateProject) {
      onCreateProject(newProjectName, newProjectDescription || undefined);
      setNewProjectName("");
      setNewProjectDescription("");
      setIsCreateProjectDialogOpen(false);
    }
  };

  const assetTypes = [
    { value: 'vocabulary', label: t('asset.vocabulary') },
    { value: 'rulesheets', label: t('asset.rulesheets') },
    { value: 'ruleflow', label: t('asset.ruleflow') },
    { value: 'rulestest', label: t('asset.rulestest') },
  ];

  return (
    <div id="projectExplorerId" className="flex flex-col h-full">
      {/* Header */}
      <div className="p-3 border-b border-gray-700">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-sm font-medium uppercase tracking-wide text-gray-400">
            {t('project.explorer')}
          </h3>
          <div className="flex space-x-1">
            {/* Create Project Button */}
            <Dialog open={isCreateProjectDialogOpen} onOpenChange={setIsCreateProjectDialogOpen}>
              <DialogTrigger asChild>
                <Button 
                  size="sm" 
                  variant="ghost" 
                  className="p-1 hover:bg-gray-600 rounded h-auto" 
                  title="New Project"
                >
                  <Folder className="h-3 w-3" />
                </Button>
              </DialogTrigger>
              <DialogContent className="bg-sidebar-bg border-gray-700 text-dark-text">
                <DialogHeader>
                  <DialogTitle className="text-dark-text">{t('project.createProject')}</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-400 mb-1">
                      {t('project.projectName')}
                    </label>
                    <Input
                      value={newProjectName}
                      onChange={(e) => setNewProjectName(e.target.value)}
                      placeholder="Enter project name"
                      className="bg-tab-bg border-gray-600 text-dark-text placeholder:text-gray-400"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-400 mb-1">
                      {t('project.description')}
                    </label>
                    <Input
                      value={newProjectDescription}
                      onChange={(e) => setNewProjectDescription(e.target.value)}
                      placeholder="Enter project description"
                      className="bg-tab-bg border-gray-600 text-dark-text placeholder:text-gray-400"
                    />
                  </div>
                  <div className="flex justify-end space-x-2">
                    <Button 
                      variant="outline" 
                      onClick={() => setIsCreateProjectDialogOpen(false)}
                      className="border-gray-600 text-gray-400 hover:bg-gray-600"
                    >
                      {t('project.cancel')}
                    </Button>
                    <Button 
                      onClick={handleCreateProject}
                      disabled={!newProjectName}
                      className="bg-github-blue hover:bg-blue-700"
                    >
                      {t('project.create')}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button 
                  size="sm" 
                  variant="ghost" 
                  className="p-1 hover:bg-gray-600 rounded h-auto" 
                  title="New Asset"
                >
                  <Plus className="h-3 w-3" />
                </Button>
              </DialogTrigger>
              <DialogContent className="bg-sidebar-bg border-gray-700 text-dark-text">
                <DialogHeader>
                  <DialogTitle className="text-dark-text">{t('asset.createAsset')}</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-400 mb-1">
                      {t('asset.assetName')}
                    </label>
                    <Input
                      value={newAssetName}
                      onChange={(e) => setNewAssetName(e.target.value)}
                      placeholder="Enter asset name"
                      className="bg-tab-bg border-gray-600 text-dark-text placeholder:text-gray-400"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-400 mb-1">
                      {t('asset.assetType')}
                    </label>
                    <Select value={newAssetType} onValueChange={setNewAssetType}>
                      <SelectTrigger className="bg-tab-bg border-gray-600 text-dark-text placeholder:text-gray-400">
                        <SelectValue placeholder={t('asset.selectType')} />
                      </SelectTrigger>
                      <SelectContent className="bg-sidebar-bg border-gray-700">
                        {assetTypes.map((type) => (
                          <SelectItem 
                            key={type.value} 
                            value={type.value}
                            className="text-dark-text hover:bg-gray-600 focus:bg-gray-600"
                          >
                            {type.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="flex justify-end space-x-2">
                    <Button 
                      variant="outline" 
                      onClick={() => setIsCreateDialogOpen(false)}
                      className="border-gray-600 text-gray-400 hover:bg-gray-600"
                    >
                      {t('project.cancel')}
                    </Button>
                    <Button 
                      onClick={handleCreateAsset}
                      disabled={!newAssetName || !newAssetType}
                      className="bg-github-blue hover:bg-blue-700"
                    >
                      {t('project.create')}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>

            <Button 
              size="sm" 
              variant="ghost" 
              className="p-1 hover:bg-gray-600 rounded h-auto" 
              title="RefreshCw"
            >
              <RefreshCw className="h-3 w-3" />
            </Button>
          </div>
        </div>

        {/* Project List */}
        {projects.length > 0 && (
          <div className="mb-3">
            <h4 className="text-xs font-medium text-gray-500 mb-2">All Projects ({projects.length})</h4>
            <div className="max-h-32 overflow-y-auto scrollbar-thin space-y-1">
              {projects.map((project) => (
                <div 
                  key={project.id}
                  className={`group flex items-center justify-between p-2 rounded cursor-pointer hover:bg-gray-600 ${
                    selectedProject?.id === project.id ? 'bg-gray-600' : ''
                  }`}
                  onClick={() => onProjectSelect(project)}
                >
                  <div className="flex items-center space-x-2 flex-1 min-w-0">
                    <Folder className="h-3 w-3 text-github-blue flex-shrink-0" />
                    <span className="text-xs truncate">{project.name}</span>
                  </div>
                  {onDeleteProject && (
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button 
                          size="sm" 
                          variant="ghost" 
                          className="p-1 h-auto w-auto hover:bg-gray-500 opacity-0 group-hover:opacity-100"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <MoreVertical className="h-3 w-3" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent className="bg-sidebar-bg border-gray-600">
                        <DropdownMenuItem 
                          className="text-xs text-red-400 hover:bg-gray-600 cursor-pointer"
                          onClick={(e) => {
                            e.stopPropagation();
                            if (confirm(`Are you sure you want to delete project "${project.name}"?`)) {
                              onDeleteProject(project);
                            }
                          }}
                        >
                          Delete Project
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Active Project Display */}
        {selectedProject ? (
          <div className="bg-tab-bg rounded border border-gray-600 p-2">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <FolderOpen className="h-4 w-4 text-warning-amber" />
                <span className="text-sm font-medium">{selectedProject.name}</span>
              </div>
              <ChevronDown className="h-3 w-3 text-gray-400" />
            </div>
            <div className="text-xs text-gray-400 mt-1">
              <i className="fas fa-git-alt mr-1"></i>main â€¢ synced
            </div>
          </div>
        ) : (
          <div className="text-sm text-gray-400">No project selected</div>
        )}
      </div>

      {/* File Explorer */}
      <div className="flex-1 overflow-y-auto scrollbar-thin">
        {isLoading ? (
          <div className="p-4 text-center text-gray-400">Loading...</div>
        ) : selectedProject ? (
          <div className="p-2">
            <div className="space-y-1">
              {assetTypes.map((folder) => {
                const folderAssets = getAssetsByType(folder.value);
                const isExpanded = expandedFolders.has(folder.value);

                return (
                  <div key={folder.value} className="text-xs">
                    {/* Folder Header */}
                    <div 
                      className="flex items-center py-1 px-2 hover:bg-gray-600 rounded cursor-pointer"
                      onClick={() => toggleFolder(folder.value)}
                    >
                      {isExpanded ? (
                        <ChevronDown className="h-3 w-3 text-gray-400" />
                      ) : (
                        <ChevronRight className="h-3 w-3 text-gray-400" />
                      )}
                      {isExpanded ? (
                        <FolderOpen className="h-4 w-4 text-warning-amber ml-1 mr-2" />
                      ) : (
                        <Folder className="h-4 w-4 text-gray-500 ml-1 mr-2" />
                      )}
                      <span>{folder.label}</span>
                      {folderAssets.length > 0 && (
                        <span className="ml-auto text-gray-500">({folderAssets.length})</span>
                      )}
                    </div>

                    {/* Folder Contents */}
                    {isExpanded && (
                      <div className="ml-6 space-y-1">
                        {folderAssets.map((asset) => (
                          <div 
                            key={asset.id}
                            className="flex items-center py-1 px-2 hover:bg-gray-600 rounded cursor-pointer group"
                            onClick={() => onAssetOpen(asset)}
                          >
                            {getAssetIcon(asset.type)}
                            <span className="ml-2 flex-1">{asset.name}</span>
                            <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100">
                              <Circle className="h-2 w-2 text-warning-amber fill-current" />
                              <DropdownMenu>
                                <DropdownMenuTrigger asChild>
                                  <Button 
                                    size="sm" 
                                    variant="ghost" 
                                    className="p-1 h-auto w-auto hover:bg-gray-500"
                                    onClick={(e) => e.stopPropagation()}
                                  >
                                    <MoreVertical className="h-3 w-3" />
                                  </Button>
                                </DropdownMenuTrigger>
                                <DropdownMenuContent className="bg-sidebar-bg border-gray-600">
                                  <DropdownMenuItem className="text-xs text-dark-text hover:bg-gray-600 cursor-pointer">
                                    Rename
                                  </DropdownMenuItem>
                                  <DropdownMenuItem className="text-xs text-dark-text hover:bg-gray-600 cursor-pointer">
                                    Duplicate
                                  </DropdownMenuItem>
                                  <DropdownMenuItem className="text-xs text-dark-text hover:bg-gray-600 cursor-pointer">
                                    Download
                                  </DropdownMenuItem>
                                  <DropdownMenuItem 
                                    className="text-xs text-red-400 hover:bg-gray-600 cursor-pointer"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      if (onDeleteAsset && confirm(`Are you sure you want to delete "${asset.name}"?`)) {
                                        onDeleteAsset(asset);
                                      }
                                    }}
                                  >
                                    Delete
                                  </DropdownMenuItem>
                                </DropdownMenuContent>
                              </DropdownMenu>
                            </div>
                          </div>
                        ))}

                        {folderAssets.length === 0 && (
                          <div className="py-2 px-2 text-gray-500 text-xs">
                            No {folder.label.toLowerCase()} files
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        ) : (
          <div className="p-4 text-center text-gray-400">
            <div className="text-4xl mb-2">ðŸ“</div>
            <p>Select a project to view files</p>
          </div>
        )}
      </div>
    </div>
  );
});

ProjectExplorer.displayName = "ProjectExplorer";

export default ProjectExplorer;

client/src/components/properties-panel.t.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

import { Settings, AlertTriangle, Users, X, Plus } from "lucide-react";
import type { Asset } from "@shared/schema";

interface PropertiesPanelProps {
  activeAsset: Asset | null;
  assets: Asset[];
}

export default function PropertiesPanel({ activeAsset, assets }: PropertiesPanelProps) {
  const [activeTab, setActiveTab] = useState<'properties' | 'validation'>('properties');
  const [assetName, setAssetName] = useState("");
  const [assetVersion, setAssetVersion] = useState("");
  const [assetDescription, setAssetDescription] = useState("");


  useEffect(() => {
    if (activeAsset) {
      setAssetName(activeAsset.name);
      // Extract version from content if available
      const content = activeAsset.content as any;
      const version = content?.rulesheet?.version || content?.vocabulary?.version || content?.ruleflow?.version || content?.rulestest?.version || "1.0";
      setAssetVersion(version);
      setAssetDescription(content?.description || "");
    }
  }, [activeAsset]);

  if (!activeAsset) {
    return (
      <div id="propertiesPanelId" className="w-80 bg-sidebar-bg border-l border-gray-700 flex items-center justify-center">
        <div className="text-center text-gray-400">
          <Settings className="h-12 w-12 mx-auto mb-4 opacity-50" />
          <p className="text-sm">Select an asset to view properties</p>
        </div>
      </div>
    );
  }

  return (
    <div id="propertiesPanelId" className="w-80 bg-sidebar-bg border-l border-gray-700 flex flex-col">
      {/* Panel Tabs */}
      <div className="flex border-b border-gray-700">
        <button 
          className={`flex-1 px-4 py-2 text-xs border-r border-gray-700 ${
            activeTab === 'properties' 
              ? 'text-dark-text bg-tab-bg' 
              : 'text-gray-400 hover:bg-gray-600'
          }`}
          onClick={() => setActiveTab('properties')}
        >
          <Settings className="h-3 w-3 mr-1 inline" />
          Properties
        </button>
        <button 
          className={`flex-1 px-4 py-2 text-xs ${
            activeTab === 'validation' 
              ? 'text-dark-text bg-tab-bg' 
              : 'text-gray-400 hover:bg-gray-600'
          }`}
          onClick={() => setActiveTab('validation')}
        >
          <AlertTriangle className="h-3 w-3 mr-1 inline" />
          Validation
        </button>
      </div>

      {/* Properties Panel */}
      {activeTab === 'properties' && (
        <div className="flex-1 p-4 text-sm text-dark-text overflow-y-auto scrollbar-thin">
          <div className="space-y-4">
            <div>
              <label className="block text-xs font-medium text-gray-400 mb-1">
                Asset Name
              </label>
              <Input 
                value={assetName}
                onChange={(e) => setAssetName(e.target.value)}
                className="w-full bg-tab-bg border border-gray-600 rounded px-3 py-2 text-sm focus:border-github-blue focus:outline-none text-dark-text"
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-400 mb-1">
                Version
              </label>
              <Input 
                value={assetVersion}
                onChange={(e) => setAssetVersion(e.target.value)}
                className="w-full bg-tab-bg border border-gray-600 rounded px-3 py-2 text-sm focus:border-github-blue focus:outline-none text-dark-text"
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-400 mb-1">
                Description
              </label>
              <Textarea 
                rows={3}
                value={assetDescription}
                onChange={(e) => setAssetDescription(e.target.value)}
                className="w-full bg-tab-bg border border-gray-600 rounded px-3 py-2 text-sm focus:border-github-blue focus:outline-none resize-none text-dark-text"
                placeholder="Asset description..."
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-400 mb-2">
                Asset References
              </label>
              <div className="space-y-2">
                <div className="flex items-center justify-between p-2 bg-tab-bg rounded border border-gray-600">
                  <span className="text-sm">Policy</span>
                  <Button size="sm" variant="ghost" className="text-red-400 hover:text-red-300 p-1 h-auto">
                    <X className="h-3 w-3" />
                  </Button>
                </div>
                <div className="flex items-center justify-between p-2 bg-tab-bg rounded border border-gray-600">
                  <span className="text-sm">Customer</span>
                  <Button size="sm" variant="ghost" className="text-red-400 hover:text-red-300 p-1 h-auto">
                    <X className="h-3 w-3" />
                  </Button>
                </div>
                <Button 
                  variant="outline"
                  className="w-full p-2 border border-dashed border-gray-600 rounded text-gray-400 hover:border-github-blue hover:text-github-blue text-sm bg-transparent h-auto"
                >
                  <Plus className="h-3 w-3 mr-1" />
                  Add Reference
                </Button>
              </div>
            </div>


          </div>
        </div>
      )}

      {/* Validation Panel */}
      {activeTab === 'validation' && (
        <div className="flex-1 p-4 text-sm text-dark-text overflow-y-auto scrollbar-thin">
          <div className="space-y-3">
            <div className="flex items-start space-x-3 p-3 bg-green-900/20 border border-green-700 rounded">
              <div className="w-2 h-2 rounded-full bg-success-green mt-2"></div>
              <div>
                <div className="text-success-green font-medium text-xs">JSON Schema Valid</div>
                <div className="text-gray-400 text-xs mt-1">Asset structure matches expected schema</div>
              </div>
            </div>

            <div className="flex items-start space-x-3 p-3 bg-yellow-900/20 border border-yellow-700 rounded">
              <div className="w-2 h-2 rounded-full bg-warning-amber mt-2"></div>
              <div>
                <div className="text-warning-amber font-medium text-xs">Warning</div>
                <div className="text-gray-400 text-xs mt-1">No description provided for this asset</div>
              </div>
            </div>

            <div className="text-center text-gray-400 py-8">
              <AlertTriangle className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p className="text-xs">All validations passed</p>
            </div>
          </div>
        </div>
      )}

      {/* Collaboration Panel */}
      <div className="border-t border-gray-700 p-4">
        <h4 className="text-xs font-medium uppercase tracking-wide text-gray-400 mb-3">
          Active Collaborators
        </h4>
        <div className="space-y-2">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 rounded-full bg-success-green flex items-center justify-center text-sm text-white font-medium">
              SC
            </div>
            <div className="flex-1">
              <div className="text-sm text-dark-text">Sarah Chen</div>
              <div className="text-xs text-gray-400">Editing line 16</div>
            </div>
            <div className="w-2 h-2 rounded-full bg-success-green"></div>
          </div>
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 rounded-full bg-github-blue flex items-center justify-center text-sm text-white font-medium">
              MJ
            </div>
            <div className="flex-1">
              <div className="text-sm text-dark-text">Mike Johnson</div>
              <div className="text-xs text-gray-400">Viewing file</div>
            </div>
            <div className="w-2 h-2 rounded-full bg-github-blue"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/top-navigation.tsx.content:
import { Button } from "@/components/ui/button";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Box, ChevronDown, Plus, Save, Upload, FileText, Trash2, FolderPlus, Sun, Moon, Globe } from "lucide-react";
import { useTheme } from "@/contexts/ThemeContext";
import { useLanguage, type Language } from "@/contexts/LanguageContext";
import type { User, Project, Asset } from "@shared/schema";

interface TopNavigationProps {
  user: User | null;
  selectedProject: Project | null;
  onProjectChange: (project: Project) => void;
  projects: Project[];
  onCreateAsset?: () => void;
  onDeleteAsset?: (asset: Asset) => void;
  activeAsset?: Asset | null;
}

export default function TopNavigation({ 
  user, 
  selectedProject, 
  onProjectChange, 
  projects,
  onCreateAsset,
  onDeleteAsset,
  activeAsset
}: TopNavigationProps) {
  const { theme, toggleTheme } = useTheme();
  const { language, setLanguage, t } = useLanguage();

  const languageNames: Record<Language, string> = {
    en: t('lang.english'),
    fr: t('lang.french'),
    de: t('lang.german'),
    es: t('lang.spanish'),
  };

  const getUserInitials = (user: User | null) => {
    if (!user) return "U";
    const firstName = user.firstName || "";
    const lastName = user.lastName || "";
    return (firstName.charAt(0) + lastName.charAt(0)).toUpperCase() || user.email?.charAt(0).toUpperCase() || "U";
  };

  return (
    <nav id="topNavigationId" className="bg-background border-b border-border h-10 flex items-center px-4 justify-between text-sm">
      <div className="flex items-center space-x-4">
        <div className="flex items-center space-x-2">
          <Box className="h-4 w-4 text-github-blue" />
          <span className="font-semibold">Corticon Studio Web</span>
        </div>

        <div className="flex items-center space-x-1">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
                {t('nav.file')}
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="bg-popover border-border">
              <DropdownMenuItem 
                onClick={onCreateAsset}
                className="text-xs text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer"
              >
                <Plus className="h-3 w-3 mr-2" />
                {t('file.newAsset')}
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => activeAsset && onDeleteAsset?.(activeAsset)}
                disabled={!activeAsset}
                className="text-xs text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <Trash2 className="h-3 w-3 mr-2" />
                {t('file.deleteAsset')}
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
          <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
            {t('nav.edit')}
          </Button>
          <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
            {t('nav.view')}
          </Button>
          <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
            {t('nav.project')}
          </Button>
          <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
            {t('nav.git')}
          </Button>
          <Button variant="ghost" size="sm" className="px-2 py-1 hover:bg-accent hover:text-accent-foreground rounded text-xs h-auto">
            {t('nav.help')}
          </Button>
        </div>
      </div>

      <div className="flex items-center space-x-3">
        {/* Project Selector */}
        {selectedProject && (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="flex items-center space-x-2 text-xs h-auto">
                <span>{selectedProject.name}</span>
                <ChevronDown className="h-3 w-3" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="bg-popover border-border">
              {projects.map((project) => (
                <DropdownMenuItem
                  key={project.id}
                  onClick={() => onProjectChange(project)}
                  className={`text-popover-foreground hover:bg-accent hover:text-accent-foreground ${selectedProject.id === project.id ? "bg-accent" : ""}`}
                >
                  {project.name}
                </DropdownMenuItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        )}

        {/* Collaboration Indicators */}
        <div className="flex items-center space-x-1">
          <div className="w-6 h-6 rounded-full bg-success-green flex items-center justify-center text-xs text-white font-medium" title="Sarah Chen - Online">
            SC
          </div>
          <div className="w-6 h-6 rounded-full bg-github-blue flex items-center justify-center text-xs text-white font-medium" title="Mike Johnson - Editing">
            MJ
          </div>
          <span className="text-xs text-gray-400">2 {t('nav.usersOnline')}</span>
        </div>

        {/* Action Buttons */}
        <Button size="sm" className="px-3 py-1 bg-github-blue hover:bg-blue-700 rounded text-xs text-white h-auto">
          <Upload className="h-3 w-3 mr-1" />
          {t('nav.saveSync')}
        </Button>

        {/* User Menu */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="sm" className="h-auto p-1">
              <Avatar className="h-6 w-6">
                <AvatarImage src={user?.profileImageUrl || undefined} />
                <AvatarFallback className="text-xs">
                  {getUserInitials(user)}
                </AvatarFallback>
              </Avatar>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="bg-popover border-border">
            <DropdownMenuItem disabled className="text-popover-foreground">
              {user?.firstName && user?.lastName 
                ? `${user.firstName} ${user.lastName}`
                : user?.email
              }
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem 
              onClick={toggleTheme}
              className="text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer"
            >
              {theme === 'dark' ? (
                <>
                  <Sun className="h-4 w-4 mr-2" />
                  {t('user.lightMode')}
                </>
              ) : (
                <>
                  <Moon className="h-4 w-4 mr-2" />
                  {t('user.darkMode')}
                </>
              )}
            </DropdownMenuItem>
            <DropdownMenuSeparator />

            {/* Language Submenu */}
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <DropdownMenuItem className="text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer flex items-center justify-between">
                  <div className="flex items-center">
                    <Globe className="h-4 w-4 mr-2" />
                    {t('user.language')}
                  </div>
                  <ChevronDown className="h-4 w-4" />
                </DropdownMenuItem>
              </DropdownMenuTrigger>
              <DropdownMenuContent side="left" align="start" className="bg-popover border-border">
                {(['en', 'fr', 'de', 'es'] as Language[]).map((lang) => (
                  <DropdownMenuItem
                    key={lang}
                    onClick={() => setLanguage(lang)}
                    className={`text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer ${
                      language === lang ? 'bg-accent text-accent-foreground' : ''
                    }`}
                  >
                    {languageNames[lang]}
                  </DropdownMenuItem>
                ))}
              </DropdownMenuContent>
            </DropdownMenu>

            <DropdownMenuSeparator />
            <DropdownMenuItem 
              onClick={() => window.location.href = '/api/logout'}
              className="text-popover-foreground hover:bg-accent hover:text-accent-foreground cursor-pointer"
            >
              {t('user.signOut')}
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </nav>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

