import { useState, useRef, useCallback } from 'react';
import { useLanguage } from '@/contexts/LanguageContext';

interface RuleflowNode {
  id: string;
  name: string;
  type: 'rulesheet';
  position: { x: number; y: number };
  size: { width: number; height: number };
}

interface RuleflowEditorProps {
  content: string;
  onChange: (content: string) => void;
}

export function RuleflowEditor({ content, onChange }: RuleflowEditorProps) {
  const { t } = useLanguage();
  const [nodes, setNodes] = useState<RuleflowNode[]>([]);
  const [draggedNode, setDraggedNode] = useState<RuleflowNode | null>(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const canvasRef = useRef<HTMLDivElement>(null);

  // Handle dropping rulesheets from explorer
  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();

    const dragData = e.dataTransfer.getData('application/json');
    if (!dragData) return;

    try {
      const asset = JSON.parse(dragData);
      if (asset.type !== 'rulesheet') return;

      const rect = canvasRef.current?.getBoundingClientRect();
      if (!rect) return;

      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      const newNode: RuleflowNode = {
        id: `node-${asset.id}-${Date.now()}`,
        name: asset.name,
        type: 'rulesheet',
        position: { x: x - 75, y: y - 25 }, // Center the node on cursor
        size: { width: 150, height: 50 }
      };

      setNodes(prev => [...prev, newNode]);
    } catch (error) {
      console.error('Failed to parse drag data:', error);
    }
  }, []);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
  }, []);

  // Handle dragging existing nodes
  const handleNodeMouseDown = useCallback((e: React.MouseEvent, node: RuleflowNode) => {
    e.preventDefault();
    const rect = canvasRef.current?.getBoundingClientRect();
    if (!rect) return;

    const offsetX = e.clientX - rect.left - node.position.x;
    const offsetY = e.clientY - rect.top - node.position.y;

    setDraggedNode(node);
    setDragOffset({ x: offsetX, y: offsetY });
  }, []);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (!draggedNode || !canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left - dragOffset.x;
    const y = e.clientY - rect.top - dragOffset.y;

    setNodes(prev => prev.map(node => 
      node.id === draggedNode.id 
        ? { ...node, position: { x, y } }
        : node
    ));
  }, [draggedNode, dragOffset]);

  const handleMouseUp = useCallback(() => {
    setDraggedNode(null);
    setDragOffset({ x: 0, y: 0 });
  }, []);

  return (
    <div className="h-full flex flex-col bg-background">
      {/* Toolbar */}
      <div className="border-b border-border p-2 flex items-center gap-2">
        <h3 className="text-sm font-medium text-foreground">
          {t('editor.ruleflow.title') || 'Ruleflow Designer'}
        </h3>
        <div className="text-xs text-muted-foreground ml-auto">
          {t('editor.ruleflow.dragHint') || 'Drag rulesheets from the explorer to add them to the flow'}
        </div>
      </div>

      {/* Canvas */}
      <div 
        ref={canvasRef}
        className="flex-1 relative overflow-hidden bg-muted/20"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        style={{ 
          backgroundImage: `
            radial-gradient(circle, hsl(var(--muted-foreground) / 0.1) 1px, transparent 1px)
          `,
          backgroundSize: '20px 20px'
        }}
      >
        {/* Grid overlay for visual reference */}
        <div className="absolute inset-0 pointer-events-none opacity-30">
          <svg width="100%" height="100%">
            <defs>
              <pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse">
                <path 
                  d="M 20 0 L 0 0 0 20" 
                  fill="none" 
                  stroke="hsl(var(--muted-foreground))" 
                  strokeWidth="0.5"
                  opacity="0.3"
                />
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#grid)" />
          </svg>
        </div>

        {/* Render nodes */}
        {nodes.map((node) => (
          <div
            key={node.id}
            className="absolute bg-card border-2 border-primary rounded-lg shadow-md cursor-move hover:border-primary/80 transition-colors"
            style={{
              left: node.position.x,
              top: node.position.y,
              width: node.size.width,
              height: node.size.height,
              userSelect: 'none'
            }}
            onMouseDown={(e) => handleNodeMouseDown(e, node)}
          >
            <div className="h-full flex items-center justify-center p-2">
              <div className="text-center">
                <div className="w-6 h-6 mx-auto mb-1 bg-primary rounded flex items-center justify-center">
                  <span className="text-xs text-primary-foreground font-bold">RS</span>
                </div>
                <div className="text-xs font-medium text-foreground truncate" title={node.name}>
                  {node.name}
                </div>
              </div>
            </div>
          </div>
        ))}

        {/* Empty state message */}
        {nodes.length === 0 && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center text-muted-foreground">
              <div className="text-lg mb-2">ðŸŽ¯</div>
              <div className="text-sm">
                {t('editor.ruleflow.emptyState') || 'Drag rulesheets from the explorer to start building your flow'}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Status bar */}
      <div className="border-t border-border p-2 text-xs text-muted-foreground">
        {t('editor.ruleflow.nodeCount') || `${nodes.length} nodes in flow`}
      </div>
    </div>
  );
}
